const redisManager  = require('../../../lib/db_manager/redis_manager');
const helper        = require('../../helper');
const ip            = require('./getIp')();

describe('lib/db_manager/redis_manager', () => {
    it('整体测试', async () => {
        await helper(async () => {
            let logger = {};
            logger = {};
            ['trace', 'debug', 'info', 'warn', 'error', 'fatal'].forEach(level => {
                logger[level] = msgOrObj => {};
            });

            let config;

            if (ip === '172.18.61.225') {
                config = {host: ip, port: 6379, password: 'CMCC10086'};
            } else if (ip === '172.31.132.146') {
                config = {host: ip, port: 6379, password: '19871102'};
            }

            let rm = redisManager(config, logger);

            await rm.close();
            return true;
        });
    });
});
